class UDPServerTest extends GroovyTestCase {
    UDPServer server = new UDPServer();

    void testStartServer() {
        server.startServer(9876);
        assert(server.socket.getLocalPort() == 9876);
    }

    void testCloseServer() {
        server.closeServer();
        assert(server.socket == null);
    }

    void testSend() {
        server.socket = new DatagramSocket(9876)
        byte[] data = [0,1,0,1,0]
        server.send(data, InetAddress.getLocalHost(), 9876)
    }

